import React, { useEffect, useState } from 'react';
import axios from 'axios';  // Import axios for making HTTP requests
import '../styles/Profile.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
  });
  const [error, setError] = useState(null);  // State to handle error messages
  const userId = JSON.parse(localStorage.getItem('user'))?.id; // Retrieve user ID from localStorage

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/user/user/${userId}`);
        setUser(response.data.user);
        setFormData(response.data.user);
      } catch (error) {
        console.error('Error fetching user data:', error);
        setError('Error fetching user data.');  // Set error message if data fetch fails
      }
    };

    if (userId) {
      fetchUserData();
    } else {
      window.location.href = '/login';
    }
  }, [userId]);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSave = async () => {
    try {
      const response = await axios.put(`http://localhost:5001/user/user/${userId}`, formData);
      setUser(response.data.user);
      localStorage.setItem('user', JSON.stringify(response.data.user));
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating user data:', error);
      setError('Error updating user data.');  // Set error message if update fails
    }
  };

  const handleCancel = () => {
    setFormData(user);
    setIsEditing(false);
  };

  if (!user) return <p>Loading...</p>;

  return (
    <div className="profile-container">
      <h2 className="profile-heading">Profile</h2>
      {error && <p className="error-message">{error}</p>} {/* Display error message if any */}
      <div className="profile-details">
        {!isEditing ? (
          <>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Phone:</strong> {user.phone}</p>
            <button onClick={handleEditClick} className="edit-button">Edit</button>
          </>
        ) : (
          <div className="edit-form">
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
              />
            </label>
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                disabled
              />
            </label>
            <label>
              Phone:
              <input
                type="text"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
              />
            </label>
            <button onClick={handleSave} className="save-button">Save</button>
            <button onClick={handleCancel} className="cancel-button">Cancel</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
