// src/components/ReviewManager.js
import React, { useState } from 'react';
import axios from 'axios';

// Setup Axios instance
const api = axios.create({
  baseURL: 'http://localhost:5000/api', // Replace with your backend base URL
});

const ReviewManager = () => {
  // State for adding review
  const [addRating, setAddRating] = useState('');
  const [addReviewText, setAddReviewText] = useState('');
  const [addEmail, setAddEmail] = useState('');
  const [addTitle, setAddTitle] = useState('');

  // State for getting reviews
  const [getTitle, setGetTitle] = useState('');
  const [reviews, setReviews] = useState([]);

  // State for deleting review
  const [deleteReviewId, setDeleteReviewId] = useState('');
  const [deleteEmail, setDeleteEmail] = useState('');

  // Handler to add a review
  const handleAddReview = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/reviews/add', {
        rating: addRating,
        review_text: addReviewText,
        email: addEmail,
        title: addTitle,
      });
      alert(response.data.message);
    } catch (error) {
      console.error('Error adding review:', error);
      alert('Failed to add review');
    }
  };

  // Handler to get reviews by book title
  const handleGetReviews = async () => {
    try {
      const response = await api.get('/reviews', {
        params: { title: getTitle },
      });
      setReviews(response.data.reviews);
    } catch (error) {
      console.error('Error fetching reviews:', error);
    }
  };

  // Handler to delete a review
  const handleDeleteReview = async () => {
    try {
      const response = await api.delete(`/reviews/${deleteReviewId}`, {
        data: { email: deleteEmail },
      });
      alert(response.data.message);
    } catch (error) {
      console.error('Error deleting review:', error);
      alert('Failed to delete review');
    }
  };

  return (
    <div>
      <h1>Review Manager</h1>

      {/* Add Review Form */}
      <div>
        <h2>Add Review</h2>
        <form onSubmit={handleAddReview}>
          <input
            type="text"
            placeholder="Email"
            value={addEmail}
            onChange={(e) => setAddEmail(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Book Title"
            value={addTitle}
            onChange={(e) => setAddTitle(e.target.value)}
            required
          />
          <input
            type="number"
            placeholder="Rating"
            value={addRating}
            onChange={(e) => setAddRating(e.target.value)}
            required
          />
          <textarea
            placeholder="Review Text"
            value={addReviewText}
            onChange={(e) => setAddReviewText(e.target.value)}
            required
          />
          <button type="submit">Add Review</button>
        </form>
      </div>

      {/* Get Reviews Form */}
      <div>
        <h2>Get Reviews</h2>
        <input
          type="text"
          placeholder="Book Title"
          value={getTitle}
          onChange={(e) => setGetTitle(e.target.value)}
        />
        <button onClick={handleGetReviews}>Get Reviews</button>

        <ul>
          {reviews.map((review) => (
            <li key={review.id}>
              <strong>{review.User.name}</strong>: {review.review_text} (Rating: {review.rating})
            </li>
          ))}
        </ul>
      </div>

      {/* Delete Review Form */}
      <div>
        <h2>Delete Review</h2>
        <input
          type="text"
          placeholder="Review ID"
          value={deleteReviewId}
          onChange={(e) => setDeleteReviewId(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Email"
          value={deleteEmail}
          onChange={(e) => setDeleteEmail(e.target.value)}
          required
        />
        <button onClick={handleDeleteReview}>Delete Review</button>
      </div>
    </div>
  );
};

export default ReviewPage;
