import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ReviewPage() {
  const [rating, setRating] = useState('');
  const [reviewText, setReviewText] = useState('');
  const [email, setEmail] = useState('');
  const [title, setTitle] = useState('');
  const [reviews, setReviews] = useState([]);
  const [reviewIdToDelete, setReviewIdToDelete] = useState('');
  const [message, setMessage] = useState('');

  // Fetch reviews for a book when title changes
  useEffect(() => {
    if (title) {
      axios
        .get(`http://localhost:5001/api/reviews`, { params: { title } })
        .then((response) => {
          setReviews(response.data.reviews);
          setMessage(response.data.message);
        })
        .catch((error) => {
          console.error('Error fetching reviews:', error);
          setMessage('Error fetching reviews');
        });
    }
  }, [title]);

  const handleAddReview = () => {
    axios
      .post('http://localhost:5001/api/reviews', {
        rating,
        review_text: reviewText,
        email,
        title,
      })
      .then((response) => {
        setMessage(response.data.message);
        setReviews([...reviews, response.data.review]); // Append new review to list
      })
      .catch((error) => {
        console.error('Error adding review:', error);
        setMessage('Error adding review');
      });
  };

  const handleDeleteReview = () => {
    axios
      .delete(`http://localhost:5001/api/reviews/${reviewIdToDelete}`, {
        data: { email },
      })
      .then((response) => {
        setMessage(response.data.message);
        setReviews(reviews.filter((review) => review.id !== reviewIdToDelete));
      })
      .catch((error) => {
        console.error('Error deleting review:', error);
        setMessage('Error deleting review');
      });
  };

  return (
    <div className="review-page">
      <h1 className="page-heading">Book Reviews</h1>
      
      <div className="review-form">
        <h2>Add a Review</h2>
        <input
          type="text"
          placeholder="Your Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="text"
          placeholder="Book Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="number"
          placeholder="Rating (1-5)"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
        />
        <textarea
          placeholder="Review Text"
          value={reviewText}
          onChange={(e) => setReviewText(e.target.value)}
        />
        <button onClick={handleAddReview}>Add Review</button>
      </div>
      
      <div className="review-list">
        <h2>Reviews</h2>
        {message && <p>{message}</p>}
        <ul>
          {reviews.map((review) => (
            <li key={review.id}>
              <p><strong>Rating:</strong> {review.rating}</p>
              <p><strong>Review:</strong> {review.review_text}</p>
              <p><strong>User:</strong> {review.User.name}</p>
            </li>
          ))}
        </ul>
      </div>

      <div className="delete-review">
        <h2>Delete a Review</h2>
        <input
          type="text"
          placeholder="Review ID to delete"
          value={reviewIdToDelete}
          onChange={(e) => setReviewIdToDelete(e.target.value)}
        />
        <button onClick={handleDeleteReview}>Delete Review</button>
      </div>
    </div>
  );
}

export default ReviewPage;
