import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Ensure you have react-router-dom installed
import '../styles/Register.css'

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [password, setPassword] = useState('');
  const [phone, setPhone] = useState('');
  const [gender, setGender] = useState('');
  const [registerMessage, setRegisterMessage] = useState('');

  const navigate = useNavigate(); // Initialize useNavigate hook

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5001/user/register', {
        name,
        email,
        address,
        password,
        phone,
        gender,
      });
      setRegisterMessage(response.data.msg);

      // Store user data in localStorage
      localStorage.setItem('user', JSON.stringify({
        name,
        email,
        address,
        phone,
        gender,
      }));

      // Redirect to login page after successful registration
      navigate('/login'); // Use navigate to redirect
    } catch (error) {
      console.error('Error registering user:', error);
      setRegisterMessage('Registration failed');
    }
  };

  return (
    <div className="registration-container">
    <h2>Register</h2>
    <form onSubmit={handleRegister} className="registration-form">
      <div className="form-group">
        <label htmlFor="name">Name</label>
        <input
          id="name"
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="email">Email</label>
        <input
          id="email"
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="address">Address</label>
        <input
          id="address"
          type="text"
          placeholder="Address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label htmlFor="password">Password</label>
        <input
          id="password"
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="phone">Phone</label>
        <input
          id="phone"
          type="text"
          placeholder="Phone"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label htmlFor="gender">Gender</label>
        <input
          id="gender"
          type="text"
          placeholder="Gender"
          value={gender}
          onChange={(e) => setGender(e.target.value)}
        />
      </div>
      <button type="submit" className="submit-button">Register</button>
      {registerMessage && <p className="register-message">{registerMessage}</p>}
    </form>
  </div>
  );
};

export default Register;
