import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/Profile.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
  });

  const userId = JSON.parse(localStorage.getItem('user'))?.id; // Retrieve user ID from localStorage

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        if (!userId) {
          console.error('No user ID found in localStorage');
          // Optionally redirect to login or handle error
          return;
        }

        const response = await axios.get(`http://localhost:5001/user//${userId}`);
        console.log('User data fetched:', response.data.user); // Debugging

        setUser(response.data.user);
        setFormData(response.data.user);
      } catch (error) {
        console.error('Error fetching user data:', error);
        // Optionally handle or display an error message
      }
    };

    fetchUserData();
  }, [userId]);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSave = async () => {
    try {
      if (!userId) {
        console.error('No user ID found in localStorage');
        // Optionally handle error
        return;
      }

      const response = await axios.put(`http://localhost:5001/user/${userId}`, formData);
      console.log('User data updated:', response.data.user); // Debugging

      setUser(response.data.user);
      localStorage.setItem('user', JSON.stringify(response.data.user));
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating user data:', error);
      // Optionally handle or display an error message
    }
  };

  const handleCancel = () => {
    setFormData(user);
    setIsEditing(false);
  };

  if (!user) return <p>Loading...</p>;

  return (
    <div className="profile-container">
      <h2 className="profile-heading">Profile</h2>
      <div className="profile-details">
        {!isEditing ? (
          <>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Phone:</strong> {user.phone}</p>
            <button onClick={handleEditClick} className="edit-button">Edit</button>
          </>
        ) : (
          <div className="edit-form">
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
              />
            </label>
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                disabled
              />
            </label>
            <label>
              Phone:
              <input
                type="text"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
              />
            </label>
            <button onClick={handleSave} className="save-button">Save</button>
            <button onClick={handleCancel} className="cancel-button">Cancel</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
