import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [updateMessage, setUpdateMessage] = useState('');

  // const navigate = useNavigate();

  useEffect(() => {
    // Fetch user details based on email stored in localStorage
    const userEmail = localStorage.getItem('userEmail');
    if (userEmail) {
      axios.get(`http://localhost:5001/user//user${encodeURIComponent(userEmail)}`)
        .then(response => {
          setUser(response.data.user);
          setName(response.data.user.name);
          setEmail(response.data.user.email);
          setPhone(response.data.user.phone);
        })
        .catch(error => console.error('Error fetching user:', error));
    }
  }, []);

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:5001/user/update/${encodeURIComponent(user.email)}`, {
        name,
        email,
        phone,
      });
      setUpdateMessage(response.data.message);
    } catch (error) {
      console.error('Error updating user:', error);
      setUpdateMessage('Update failed');
    }
  };

  if (!user) return <p>Loading...</p>;

  return (
    <div className="user-profile-container">
      <h2>User Profile</h2>
      <form onSubmit={handleUpdate} className="user-profile-form">
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input id="name" type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input id="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        </div>
        <div className="form-group">
          <label htmlFor="phone">Phone</label>
          <input id="phone" type="text" value={phone} onChange={(e) => setPhone(e.target.value)} />
        </div>
        <button type="submit">Update</button>
        {updateMessage && <p className="update-message">{updateMessage}</p>}
      </form>
    </div>
  );
};

export default Profile;
